import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const DadosPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  const [contactMethod, setContactMethod] = useState('');
  const [email, setEmail] = useState('');
  const [whatsapp, setWhatsapp] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [step, setStep] = useState(1); // 1: entrada de dados, 2: confirmaÃ§Ã£o, 3: sucesso
  const [paymentData, setPaymentData] = useState(null);

  // Capturar dados do pagamento da URL ou state
  useEffect(() => {
    const urlParams = new URLSearchParams(location.search);
    const paymentId = urlParams.get('payment_id') || location.state?.paymentId;
    const status = urlParams.get('status') || location.state?.status;
    const valor = location.state?.valor;
    const festa = location.state?.festa;

    if (paymentId && status) {
      setPaymentData({
        paymentId,
        status,
        valor,
        festa
      });
      
      // Se nÃ£o hÃ¡ dados de pagamento vÃ¡lidos, redirecionar para home
      if (status !== 'approved') {
        console.warn('Status do pagamento nÃ£o Ã© aprovado:', status);
        navigate('/', { replace: true });
      }
    } else {
      // Se nÃ£o hÃ¡ dados de pagamento, usar dados simulados para desenvolvimento
      const mockPaymentData = {
        paymentId: 'MP-12345678',
        status: 'approved',
        valor: 150.00,
        festa: {
          nome: 'Festa de VerÃ£o 2025'
        }
      };
      setPaymentData(mockPaymentData);
    }
  }, [location, navigate]);

  // Phone input mask para formato brasileiro (DDD + 9 dÃ­gitos)
  const formatWhatsAppNumber = (input) => {
    const numericInput = input.replace(/\D/g, '');
    
    if (numericInput.length <= 2) {
      return numericInput;
    } else if (numericInput.length <= 7) {
      return `(${numericInput.slice(0, 2)}) ${numericInput.slice(2)}`;
    } else {
      return `(${numericInput.slice(0, 2)}) ${numericInput.slice(2, 7)}-${numericInput.slice(7, 11)}`;
    }
  };

  const handleWhatsAppChange = (e) => {
    setWhatsapp(formatWhatsAppNumber(e.target.value));
  };

  // ValidaÃ§Ã£o de e-mail
  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };
  
  // ValidaÃ§Ã£o de WhatsApp (DDD + 9 dÃ­gitos = 11 dÃ­gitos no total)
  const validateWhatsApp = (number) => {
    const numericOnly = number.replace(/\D/g, '');
    return numericOnly.length === 11;
  };

  // FunÃ§Ã£o para ir para a etapa de confirmaÃ§Ã£o
  const handleProceedToConfirmation = () => {
    setError('');
    
    if (contactMethod === 'email') {
      if (!validateEmail(email)) {
        setError('Por favor, insira um e-mail vÃ¡lido');
        return;
      }
    } else if (contactMethod === 'whatsapp') {
