        throw new Error(`Erro HTTP: ${response.status}`);
      }

      const data = await response.json();
      console.log('Dados recebidos:', data);

      if (data.success) {
        setStep(3);
      } else {
        setError(data.message || 'Erro ao enviar ingresso');
      }
    } catch (error) {
      console.error('Erro ao conectar com o servidor:', error);
      
      // Mensagem de erro mais especÃ­fica
      if (error.message.includes('fetch')) {
        setError('Erro de conexÃ£o. Verifique se o servidor backend estÃ¡ rodando na porta 3001.');
      } else {
        setError('Erro de conexÃ£o. Verifique se o servidor estÃ¡ rodando.');
